-- Create table of papers
CREATE TABLE public.papers
(
    id_paper serial,
    name character varying(254),
    -- Begin Bibtex fields http://newton.ex.ac.uk/tex/pack/bibtex/btxdoc/node6.html
    address character varying(254),
    author character varying(254),
    booktitle character varying(254),
    chapter character varying(254),
    edition character varying(254),
    editor character varying(254),
    howpublished character varying(254),
    institution character varying(254),
    journal character varying(254),
    month character varying(254),
    note character varying(254),
    number character varying(254),
    organization character varying(254),
    isbn character varying(254),
    issn character varying(254),
    url character varying(254),
    urn character varying(254),
    doi character varying(254),
    pages character varying(254),
    publisher character varying(254),
    school character varying(254),
    series character varying(254),
    title character varying(254),
    type character varying(254),
    volume character varying(254),
    year character varying(254),
    key character varying(254),
    file character varying(254),
    ee character varying(254),
    crossref character varying(254),
    bibsource character varying(254),
    issue_date character varying(254),
    abstract character varying(254),
    numpages character varying(254),
    editors character varying(254),
    location character varying(254),
    collection character varying(254),
    pmid character varying(254),
    -- End Bibtex fields
    id_user bigint,
    id_field bigint,
    creation_date date,
    categories character varying(254),
    keywords character varying(254),
    ethics4EU character varying(254),
    pdf oid,
    PRIMARY KEY (id_paper)
);

ALTER TABLE IF EXISTS public.papers
    OWNER to postgres;

